public void XXXtestBGSearchTaskThreads() throws Exception {
  String algLines[] = {
      "log.time.step.msec = 100",
      "log.step=100000",
      "ResetSystemErase",
      "CreateIndex",
      "{ AddDoc } : 1000",
      "Optimize",
