/** add a field for facet computation
 * 
 * @param f the field name from the IndexSchema
 */
public void addFacetField(String ... fields) {
  for( String f : fields ) {
    this.add(FacetParams.FACET_FIELD, f);
  }
  this.set(FacetParams.FACET, true);
}

