/**
 * Test that norms values are preserved as the index is maintained.
 * Including separate norms.
 * Including merging indexes with seprate norms. 
 * Including optimize. 
 */
public void testNorms() throws IOException {
  Directory dir1 = new RAMDirectory();

  norms = new ArrayList<Float>();
  modifiedNorms = new ArrayList<Float>();

  createIndex(dir1);
  doTestNorms(dir1);

  // test with a single index: index2
  ArrayList<Float> norms1 = norms;
  ArrayList<Float> modifiedNorms1 = modifiedNorms;
  int numDocNorms1 = numDocNorms;

  norms = new ArrayList<Float>();
  modifiedNorms = new ArrayList<Float>();
  numDocNorms = 0;

  Directory dir2 = new RAMDirectory();

  createIndex(dir2);
  doTestNorms(dir2);

  // add index1 and index2 to a third index: index3
  Directory dir3 = new RAMDirectory();

  createIndex(dir3);
  IndexWriter iw = new IndexWriter(dir3, new IndexWriterConfig(
      TEST_VERSION_CURRENT, anlzr).setOpenMode(OpenMode.APPEND)
      .setMaxBufferedDocs(5));
  ((LogMergePolicy) iw.getMergePolicy()).setMergeFactor(3);
  iw.addIndexesNoOptimize(new Directory[]{dir1,dir2});
  iw.optimize();
  iw.close();

  norms1.addAll(norms);
  norms = norms1;
  modifiedNorms1.addAll(modifiedNorms);
  modifiedNorms = modifiedNorms1;
  numDocNorms += numDocNorms1;

  // test with index3
  verifyIndex(dir3);
  doTestNorms(dir3);

  // now with optimize
  iw = new IndexWriter(dir3, new IndexWriterConfig(TEST_VERSION_CURRENT,
      anlzr).setOpenMode(OpenMode.APPEND).setMaxBufferedDocs(5));
  ((LogMergePolicy) iw.getMergePolicy()).setMergeFactor(3);
  iw.optimize();
  iw.close();
  verifyIndex(dir3);

  dir1.close();
  dir2.close();
  dir3.close();
}

