private final void openNorms(Directory useDir) throws IOException {
  for (int i = 0; i < fieldInfos.size(); i++) {
    FieldInfo fi = fieldInfos.fieldInfo(i);
    if (fi.isIndexed)
      norms.put(fi.name,
                new Norm(useDir.openFile(segment + ".f" + fi.number)));
  }
}

