  assert reusableToken != null;
  if (!ngrams.isEmpty()) {
      return (Token)ngrams.removeFirst();
  }

  Token token = null;

  while (ngrams.isEmpty() && (token = input.next()) != null) {
      ngram(token);
  }

  if (token == null) {
      return null;
  }

  if (!ngrams.isEmpty()) {
      return (Token)ngrams.removeFirst();
  } else {
      return null;
  }
}

