@Override
public long get(long index) {
  try {
    long offset = (index * 12) >>> 3;
    int shift = (int) ((index + 1) & 1) << 2;
    return (in.readShort(offset) >>> shift) & 0xFFF;
  } catch (IOException e) {
    throw new RuntimeException(e);
  }
}

