public static void assertQEx(String message, SolrQueryRequest req, SolrException.ErrorCode code ) {
  try {
    h.query(req);
    fail( message );
  } catch (SolrException e) {
    assertEquals( code.code, e.code() );
  } catch (Exception e2) {
    throw new RuntimeException("Exception during query", e2);
  }
}

