void parseCondition(String s, Object o) {
  if (IGNORE_TAGS.contains(s)) return;
  if (tag != null) {
    throw new IllegalArgumentException("Only one tag other than collection, shard, replica is possible");
  }
  tag = parse(s, singletonMap(s, o));
}

