private long dvRamBytesUsed() {
  long ramBytesUsed = 0;
  for (RefCount<DocValuesProducer> dvp : genDVProducers.values()) {
    ramBytesUsed += dvp.get().ramBytesUsed();
  }
  return ramBytesUsed;
}

/** Returns approximate RAM bytes used. */
synchronized long ramBytesUsed() {
  long ramBytesUsed = 0;
  for (RefCount<DocValuesProducer> dvp : genDVProducers.values()) {
    ramBytesUsed += dvp.get().ramBytesUsed();
  }
  return ramBytesUsed;
}

