/** flips a bit, expanding the set size if necessary */
public void flip(long index) {
  int wordNum = expandingWordNum(index);
  int bit = (int)index & 0x3f;       // mod 64
  long bitmask = 1L << bit;
  bits[wordNum] ^= bitmask;
}

