public Direct16(DataInput in, int valueCount) throws IOException {
  super(valueCount, BITS_PER_VALUE);
  short[] values = new short[valueCount];
  for(int i=0;i<valueCount;i++) {
    values[i] = in.readShort();
  }
  final int mod = valueCount % 4;
  if (mod != 0) {
    final int pad = 4-mod;
    // round out long
    for(int i=0;i<pad;i++) {
      in.readShort();
    }
  }

  this.values = values;
}

