/**
 * Determines the type of the given character
 *
 * @param ch Character whose type is to be determined
 * @return Type of the character
 */
private int charType(int ch) {
  if (ch < charTypeTable.length) {
    return charTypeTable[ch];
  }
  return getType(ch);
}

