            cmd.waitFlush = StrUtils.parseBoolean(attrVal);
            sawWaitFlush=true;
          } else if ("waitSearcher".equals(attrName)) {
            cmd.waitSearcher = StrUtils.parseBoolean(attrVal);
            sawWaitSearcher=true;
          } else {
            log.warning("unexpected attribute commit/@" + attrName);
          }
        }

