synchronized long updateSlice(DeleteSlice slice) {
  long seqNo = getNextSequenceNumber();
  if (slice.sliceTail != tail) {
    // new deletes arrived since we last checked
    slice.sliceTail = tail;
    seqNo = -seqNo;
  }
  return seqNo;
}

