public int read(char[] cbuf, int off, int len) throws IOException {
  final int charsRead = super.read(cbuf, off, len);
  if (charsRead > 0) {
    final int end = off + charsRead;
    while (off < end) {
      if (cbuf[off] == '\u200C')
        cbuf[off] = ' ';
      off++;
    }
  }
  return charsRead;
}

