public void testExpungeDeletes() throws IOException {
  Directory dir = new MockRAMDirectory();
  IndexWriter writer = new IndexWriter(dir, new IndexWriterConfig(
      TEST_VERSION_CURRENT, new StandardAnalyzer(TEST_VERSION_CURRENT))
      .setMaxBufferedDocs(2).setRAMBufferSizeMB(
          IndexWriterConfig.DISABLE_AUTO_FLUSH));

  Document document = new Document();

  document = new Document();
  Field storedField = new Field("stored", "stored", Field.Store.YES,
                                Field.Index.NO);
  document.add(storedField);
  Field termVectorField = new Field("termVector", "termVector",
                                    Field.Store.NO, Field.Index.NOT_ANALYZED,
                                    Field.TermVector.WITH_POSITIONS_OFFSETS);
  document.add(termVectorField);
  for(int i=0;i<10;i++)
    writer.addDocument(document);
  writer.close();

  IndexReader ir = IndexReader.open(dir, false);
  assertEquals(10, ir.maxDoc());
  assertEquals(10, ir.numDocs());
  ir.deleteDocument(0);
  ir.deleteDocument(7);
  assertEquals(8, ir.numDocs());
  ir.close();

  writer = new IndexWriter(dir, new IndexWriterConfig(TEST_VERSION_CURRENT, new StandardAnalyzer(TEST_VERSION_CURRENT)));
  assertEquals(8, writer.numDocs());
  assertEquals(10, writer.maxDoc());
  writer.expungeDeletes();
  assertEquals(8, writer.numDocs());
  writer.close();
  ir = IndexReader.open(dir, true);
  assertEquals(8, ir.maxDoc());
  assertEquals(8, ir.numDocs());
  ir.close();
  dir.close();
}

