public static String randomSimpleString(Random r) {
  final int end = r.nextInt(10);
  if (end == 0) {
    // allow 0 length
    return "";
  }
  final char[] buffer = new char[end];
  for (int i = 0; i < end; i++) {
    buffer[i] = (char) _TestUtil.nextInt(r, 97, 102);
  }
  return new String(buffer, 0, end);
}

