/** Invert all bits */
public void invertAll() {
  if (count != -1) {
    count = size - count;
  }
  if (bits.length > 0) {
    for(int idx=0;idx<bits.length;idx++) {
      bits[idx] = (byte) (~bits[idx]);
    }
    clearUnusedBits();
  }
}

