synchronized void abortFullFlushes() {
  try {
    for (DocumentsWriterPerThread dwpt : flushQueue) {
      doAfterFlush(dwpt);
    }
    for (DocumentsWriterPerThread dwpt : blockedFlushes) {
      doAfterFlush(dwpt);
    }
  } finally {
    fullFlush = false;
  }
}

