public void testEscapeExtension() {
  assertEquals("abc\\:\\?\\{\\}\\[\\]\\\\\\(\\)\\+\\-\\!\\~", ext
      .escapeExtensionField("abc:?{}[]\\()+-!~"));
  try {
    ext.escapeExtensionField(null);
    fail("should throw NPE - escape string is null");
  } catch (NullPointerException e) {
    // 
  }
}

