DocumentsWriterPerThread newDocumentsWriterPerThread() {
  DocumentsWriterPerThread perThread = new DocumentsWriterPerThread(directory, this, config
      .getIndexingChain());
  sequenceIDLock.lock();
  try {
    numDocumentsWriterPerThreads++;
    return perThread;
  } finally {
    sequenceIDLock.unlock();
  }
}

