@Override
public Scorer scorer(LeafReaderContext context) throws IOException {
  Spans spans = getSpans(context, Postings.PAYLOADS);
  if (spans == null)
    return null;
  return new PayloadSpanScorer(spans, this, innerWeight.getSimScorer(context));
}

