/** For testing */
public final int getRecomputedCount() {
  int c = 0;
  int end = bits.length;
  for (int i = 0; i < end; i++) {
    c += BYTE_COUNTS[bits[i] & 0xFF];	  // sum bits per byte
  }
  return c;
}

