private void publishCoresAsDown() {
  synchronized (cores) {
    for (SolrCore core : cores.values()) {
      try {
        zkController.publish(core.getCoreDescriptor(), ZkStateReader.DOWN);
      } catch (KeeperException e) {
        log.error("", e);
      } catch (InterruptedException e) {
        log.error("", e);
      }
    }
  }
}

