  List<SolrCore> cores = solrCores.getCores();

  for (SolrCore core : cores) {
    try {
      zkController.publish(core.getCoreDescriptor(), ZkStateReader.DOWN);
    } catch (KeeperException e) {
      CoreContainer.log.error("", e);
    } catch (InterruptedException e) {
      CoreContainer.log.error("", e);
    }
  }
}

