@Override
public UninvertingReader.Type getUninversionType(SchemaField sf) {
  if (sf.multiValued()) {
    return UninvertingReader.Type.SORTED_LONG;
  } else {
    return UninvertingReader.Type.LONG_POINT;
  }
}

@Override
public UninvertingReader.Type getUninversionType(SchemaField sf) {
  if (sf.multiValued()) {
    return null;
  } else {
    return UninvertingReader.Type.LONG_POINT;
  }
}

