@Override
public boolean incrementToken() throws IOException {
  if (input.incrementToken()) {
    if (!keywordAttr.isKeyword()) {
      final String term = termAtt.toString().toLowerCase(Locale.ENGLISH);
      termAtt.setEmpty().append(term);
    }
    return true;
  }
  return false;
}

