private int rollup(int ord) throws IOException {
  int[] children = getChildren();
  int[] siblings = getSiblings();
  int sum = 0;
  while (ord != TaxonomyReader.INVALID_ORDINAL) {
    increment(ord, rollup(children[ord]));
    sum += getValue(ord);
    ord = siblings[ord];
  }
  return sum;
}

