public void close() {
  if (zkController != null) {
    zkController.close();
  }

  if (zkServer != null) {
    zkServer.stop();
  }
}

public void close() {

  try {
    if (zkController != null) {
      zkController.close();
    }
  } finally {
    try {
      if (zkServer != null) {
        zkServer.stop();
      }
    } finally {
      ExecutorUtil.shutdownNowAndAwaitTermination(coreZkRegister);
    }
  }

}

