static int bytesToInt(byte[] src, int index) {
  int x = 0;
  for(int i=0;i<4;i++) {
    x |= (src[4*index+i] & 0xff) << (24-i*8);
  }
  // Re-flip the sign bit to restore the original value:
  return x ^ 0x80000000;
}

