public WindowsIndexInput(Path file, int bufferSize) throws IOException {
  super("WindowsIndexInput(path=\"" + file + "\")", bufferSize);
  fd = WindowsDirectory.open(file.toString());
  length = WindowsDirectory.length(fd);
  isOpen = true;
}

