protected static int writePrefixLength(DataOutput datOut, BytesRef bytes)
    throws IOException {
  if (bytes.length < 128) {
    datOut.writeByte((byte) bytes.length);
    return 1;
  } else {
    datOut.writeByte((byte) (0x80 | (bytes.length >> 8)));
    datOut.writeByte((byte) (bytes.length & 0xff));
    return 2;
  }
}

