/* Return a char[] to the pool */
synchronized void recycleCharBlocks(char[][] blocks, int numBlocks) {
  for(int i=0;i<numBlocks;i++)
    freeCharBlocks.add(blocks[i]);
  numBytesUsed -= numBlocks * CHAR_BLOCK_SIZE * CHAR_NUM_BYTE;
}

