/** In flush we reset the fieldHash to not maintain per-field state
 *  across segments */
@Override
void doAfterFlush() {
  fieldHash = new DocFieldProcessorPerField[2];
  hashMask = 1;
  totalFieldCount = 0;
  perDocConsumer = null;
  docValues.clear();
}

