@Override
public int nextDoc() throws IOException {
  while(true) {
    if (current == null) {
      if (upto == numSubs-1) {
        return this.doc = NO_MORE_DOCS;
      } else {
        upto++;
        current = subs[upto].postingsEnum;
        currentBase = subs[upto].slice.start;
      }
    }

    final int doc = current.nextDoc();
    if (doc != NO_MORE_DOCS) {
      return this.doc = currentBase + doc;
    } else {
      current = null;
    }
  }
}

