 */
public StorageEntryWrapper getEntry(final String entryId,
		final String feedId) {
	this.readLock.lock();
	try {

		if (this.bufferMap.containsKey(feedId))
			return this.bufferMap.get(feedId).get(entryId);
		return null;

	} finally {
		this.readLock.unlock();
	}
}

