@Override
public long ramBytesUsed() {

  // Sync only to pull the current set of values:
  List<DocIdSet> docIdSets;
  synchronized(cache) {
    docIdSets = new ArrayList<>(cache.values());
  }

  long total = 0;
  for(DocIdSet dis : docIdSets) {
    total += dis.ramBytesUsed();
  }

  return total;
}

