public double getStandardDeviation()
{
  if( count <= 1.0D ) 
    return 0.0D;

  return Math.sqrt( ( ( count * sumOfSquares ) - ( sum * sum ) )
                    / ( count * ( count - 1.0D ) ) );  
}

