      int from = (int) slices[0].start;
      int to = from + (int) slices[0].count;
      Arrays.fill(commonPrefixLengths, numIndexDims, numDataDims, bytesPerDim);
      heapSource.readPackedValue(from, scratch1);
      for (int i = from + 1; i < to; ++i) {
