    int count = Math.toIntExact(source.count);
    assert count > 0: "nodeID=" + nodeID + " leafNodeOffset=" + leafNodeOffset;
    writeLeafBlockDocs(out, heapSource.docIDs, Math.toIntExact(source.start), count);

