@Override
protected BytesRef changeToken(BytesRef in) {
  int upto = 0;
  for(int i=0;i<in.length;i++) {
    byte b = in.bytes[in.offset+i];
    if (b == (byte) 0xff) {
      if (spare.bytes.length == upto) {
        spare.grow(upto+2);
      }
      spare.bytes[upto++] = (byte) 0xff;
      spare.bytes[upto++] = b;
    } else {
      if (spare.bytes.length == upto) {
        spare.grow(upto+1);
      }
      spare.bytes[upto++] = b;
    }
  }
  spare.offset = 0;
  spare.length = upto;
  return spare;
}

