    id = doc.getFieldValue( uniqueKeyField.getName() );
  }
  cmd.doc = builder.build( doc );
  updateHandler.addDoc(cmd);
  response.add( "added", id );

  long now = System.currentTimeMillis();
  log.info("added id={" + id  + "} in " + (now - start) + "ms  ["+(now-startTime)+"]");
}

