final void close() throws IOException {
  if (doClose) {

    try {
      if (fieldsStream != null) {
        try {
          fieldsStream.close();
        } finally {
          fieldsStream = null;
        }
      }
    } catch (IOException ioe) {
      try {
        if (indexStream != null) {
          try {
            indexStream.close();
          } finally {
            indexStream = null;
          }
        }
      } catch (IOException ioe2) {
        // Ignore so we throw only first IOException hit
      }
      throw ioe;
    } finally {
      if (indexStream != null) {
        try {
          indexStream.close();
        } finally {
          indexStream = null;
        }
      }
    }
  }
}

