public void doIntegrationTest() throws Exception {
  final long minGB = (random().nextBoolean() ? 1 : 0);
  assumeTrue("doIntegrationTest needs minGB="+minGB+" usable disk space",
      ImplicitSnitch.getUsableSpaceInGB(Paths.get("/")) > minGB);

