@Override
public boolean incrementToken() throws IOException {
  if (pending != null) {
    restoreState(pending);
    termAtt.setEmpty().append('B');
    posIncAtt.setPositionIncrement(0);
    pending = null;
    return true;
  }
  if (!input.incrementToken()) {
    return false;
  }
  if (termAtt.toString().equals("b")) {
    pending = captureState();
  }
  return true;
}

