public static String getHex( int [] raw, int sz ) {
  if ( raw == null ) {
    return null;
  }
  final StringBuilder hex = new StringBuilder( 4 * raw.length );
  for ( int i=0; i<sz; i++ ) {
    if (i>0 && i%8 == 0)
      hex.append("\n");
    hex.append(String.format("%08x ",raw[i]));
  }
  return hex.toString();
}

