  if (Strings.isNullOrEmpty(field) && Strings.isNullOrEmpty(defaultField)) {
    throw new SolrException
        (SolrException.ErrorCode.BAD_REQUEST,
            "no field name specified in query and no default specified via 'df' param");
  }
}

