public static FieldNameSelector createFieldNameSelector
  (final SolrResourceLoader loader,
   final SolrCore core,
   final SelectorParams params,
   final FieldNameSelector defSelector) {

  if (params.noSelectorsSpecified()) {
    return defSelector;
