@Override
public boolean incrementToken() throws IOException {
  int count = input.read(buffer);
  if (done)
    return false;
  else {
    done = true;
    if (count == 1) {
      termAtt.termBuffer()[0] = buffer[0];
      termAtt.setTermLength(1);
    } else
      termAtt.setTermLength(0);
    return true;
  }
}

