  assertTrue(Arrays.equals(encodedABC, encodedCBA));
  //[b,a,c]
  ShapeField.encodeTriangle(b, byEnc, bxEnc, ayEnc, axEnc, cyEnc, cxEnc);
  int[] encodedBAC= new int[6];
